// src/items/items.controller.ts
import { Controller, Post, Body, ParseIntPipe, ValidationPipe, UsePipes, Get, Param } from '@nestjs/common';
import { CreateItemDto } from './dto/create-item.dto'; // Assumes CreateItemDto from previous example

@Controller('items')
export class ItemsController {
  // Pipe applied to a specific parameter
  @Post()
  create(@Body(new ValidationPipe()) createItemDto: CreateItemDto) {
    return `Received item: ${createItemDto.name}, Quantity: ${createItemDto.quantity}`;
  }

  // Pipe applied to a route parameter
  @Get(':id')
  findOne(@Param('id', ParseIntPipe) id: number) {
    // 'id' is now guaranteed to be a number or an error would have been thrown
    return `Fetching item with ID: ${id} (Type: ${typeof id})`;
  }

  // Pipe applied at the method level (applies to all parameters with DTOs)
  // @Post('another')
  // @UsePipes(new ValidationPipe())
  // createAnother(@Body() createItemDto: CreateItemDto) { /* ... */ }
}

