// server.js
const express = require('express');
const path = require('path'); // Node.js path module
const app = express();
const port = 3000;

// Serve static files from the 'public' directory
app.use(express.static(path.join(__dirname, 'public')));

// Example: If you have public/index.html, it will be served at '/'
// If you have public/css/style.css, it will be served at '/css/style.css'

app.get('/api/data', (req, res) => { // An API route
  res.json({ message: 'Some API data' });
});

app.listen(port, () => {
  console.log(`Server running at http://localhost:${port}`);
  console.log('Serving static files from ./public directory');
});

// Create a 'public' folder in the same directory as server.js
// Inside 'public', you can have:
// - index.html (e.g., <h1>Welcome!</h1>)
// - css/style.css (e.g., body { background-color: lightblue; })

const express = require('express');
const app = express();
const port = 3000;

// Route with a 'userId' parameter
app.get('/users/:userId', (req, res) => {
  const userId = req.params.userId;
  res.send(`User profile for ID: ${userId}`);
});

// Route with multiple parameters
app.get('/books/:bookId/author/:authorId', (req, res) => {
  const bookId = req.params.bookId;
  const authorId = req.params.authorId;
  res.send(`Book ID: ${bookId}, Author ID: ${authorId}`);
});

app.listen(port);

