// Basic HTML setup: <body style="margin:0;"><canvas id="myCanvas"></canvas></body>
// Import Three.js (e.g., via CDN or npm import * as THREE from 'three';)

// 1. Scene
const scene = new THREE.Scene();
scene.background = new THREE.Color(0xeeeeee); // Light gray background

// 2. Camera
// PerspectiveCamera(field of view, aspect ratio, near clip, far clip)
const camera = new THREE.PerspectiveCamera(75, window.innerWidth / window.innerHeight, 0.1, 1000);
camera.position.z = 5; // Move camera back a bit

// 3. Renderer
const renderer = new THREE.WebGLRenderer({ canvas: document.getElementById('myCanvas'), antialias: true });
renderer.setSize(window.innerWidth, window.innerHeight);

// Animation loop (or render once)
function animate() {
  requestAnimationFrame(animate);
  // Any updates to objects or camera would go here
  renderer.render(scene, camera);
}

// Handle window resize
window.addEventListener('resize', () => {
  camera.aspect = window.innerWidth / window.innerHeight;
  camera.updateProjectionMatrix();
  renderer.setSize(window.innerWidth, window.innerHeight);
});

animate();

