// Assume scene, camera, renderer, and a 'cube' mesh are set up
// scene.add(cube);
// camera.position.z = 5;

function animate() {
  // Schedule the next frame
  requestAnimationFrame(animate);

  // Update object properties for animation
  if (typeof cube !== 'undefined') { // Check if cube exists
    cube.rotation.x += 0.01;
    cube.rotation.y += 0.005;
  }

  // Render the scene
  renderer.render(scene, camera);
}

// Start the animation loop if a renderer exists
if (typeof renderer !== 'undefined') {
    animate();
} else {
    console.error('Renderer not initialized for animation loop.');
}

