  width: 800,         // default: 800
  height: 600,        // default: 600
  backgroundColor: 0x1099bb, // light blue background
  // resolution: window.devicePixelRatio || 1, // for crisp rendering on high DPI displays
  // autoDensity: true, // automatically set resolution based on devicePixelRatio
});
document.body.appendChild(app.view); // Add the canvas to the DOM (app.view is the canvas element)

// 2. Load an image texture and create a Sprite
// (Ensure you have an image, e.g., 'assets/bunny.png')
PIXI.Assets.load('assets/bunny.png').then((texture) => {
  // This creates a new Sprite from the loaded texture
  const bunny = new PIXI.Sprite(texture);

  // Center the sprite's anchor point
  bunny.anchor.set(0.5);

  // Move the sprite to the center of the screen
  bunny.x = app.screen.width / 2;
  bunny.y = app.screen.height / 2;

  // Add the sprite to the stage (root container)
  app.stage.addChild(bunny);

  // Optional: Add a ticker for animation
  app.ticker.add((delta) => {
    // bunny.rotation += 0.01 * delta; // Rotate the bunny
  });
});

