import React, { useState, useEffect } from 'react';

function Timer() {
  const [seconds, setSeconds] = useState(0);

  useEffect(() => {
    // This effect runs after every render where 'seconds' has changed
    // or on mount if dependency array is empty.
    const intervalId = setInterval(() => {
      setSeconds(prevSeconds => prevSeconds + 1);
    }, 1000);

    // Cleanup function: runs when component unmounts or before effect re-runs
    return () => clearInterval(intervalId);
  }, []); // Empty dependency array: effect runs only once on mount, cleans up on unmount

  return <p>Timer: {seconds}s</p>;
}

