function TemperatureInput({ scale, temperature, onTemperatureChange }) {
  return (
    <fieldset>
      <legend>Enter temperature in {scale}:</legend>
      <input value={temperature} onChange={(e) => onTemperatureChange(e.target.value, scale)} />
    </fieldset>
  );
}

function Calculator() { // Common ancestor
  const [temperature, setTemperature] = useState('');
  const [scale, setScale] = useState('c');

  const toCelsius = (f) => (f - 32) * 5 / 9;
  const toFahrenheit = (c) => (c * 9 / 5) + 32;

  const handleCelsiusChange = (temp) => {
    setTemperature(temp);
    setScale('c');
  };
  const handleFahrenheitChange = (temp) => {
    setTemperature(temp);
    setScale('f');
  };

  const celsius = scale === 'f' ? toCelsius(parseFloat(temperature)) || '' : temperature;
  const fahrenheit = scale === 'c' ? toFahrenheit(parseFloat(temperature)) || '' : temperature;

  return (
    <div>
      <TemperatureInput scale="Celsius" temperature={celsius} onTemperatureChange={handleCelsiusChange} />
      <TemperatureInput scale="Fahrenheit" temperature={fahrenheit} onTemperatureChange={handleFahrenheitChange} />
    </div>
  );
}

