// File: pages/api/items/[itemId].js
export default function itemHandler(req, res) {
  const { itemId } = req.query; // Extract itemId from the query

  if (req.method === 'GET') {
    // Fetch item data based on itemId from a database or service
    res.status(200).json({ id: itemId, name: `Item ${itemId}`, price: Math.random() * 100 });
  } else if (req.method === 'PUT') {
    // Update item data for itemId
    res.status(200).json({ message: `Item ${itemId} updated.`, data: req.body });
  } else {
    res.setHeader('Allow', ['GET', 'PUT']);
    res.status(405).end(`Method ${req.method} Not Allowed`);
  }
}

