function processValue(value: string | number | string[]) {
  // typeof type guard
  if (typeof value === 'string') {
    console.log(value.toUpperCase());
  } 
  // Array.isArray type guard (more reliable than typeof for arrays)
  else if (Array.isArray(value)) {
    console.log("Array items:", value.join(', '));
  }
  // instanceof type guard (for classes)
  // else if (value instanceof Date) { console.log(value.getFullYear()); }
  else {
    // Here, TypeScript knows 'value' must be a number
    console.log(value.toFixed(2));
  }
}

processValue("typescript");
processValue(123.456);
processValue(["a", "b", "c"]);

