// src/common/interceptors/transform.interceptor.ts
import { Injectable, NestInterceptor, ExecutionContext, CallHandler } from '@nestjs/common';
import { Observable } from 'rxjs';
import { map } from 'rxjs/operators';

export interface Response<T> {
  statusCode: number;
  message?: string;
  data: T;
}

@Injectable()
export class TransformInterceptor<T> implements NestInterceptor<T, Response<T>> {
  intercept(context: ExecutionContext, next: CallHandler): Observable<Response<T>> {
    const httpContext = context.switchToHttp();
    const response = httpContext.getResponse();
    const statusCode = response.statusCode; // Get status code set by controller or default

    return next.handle().pipe(
      map(data => ({
        statusCode: statusCode,
        message: 'Success', // Or derive from data/context
        data: data 
      }))
    );
  }
}

// Usage in main.ts or a specific controller/method:
// app.useGlobalInterceptors(new TransformInterceptor());
// or @UseInterceptors(TransformInterceptor) on controller/method

