import { Controller, Get, Param, ParseIntPipe, NotFoundException } from '@nestjs/common';

@Controller('items')
export class ItemsController {
  private items = [
    { id: 1, name: 'Item A' },
    { id: 2, name: 'Item B' },
  ];

  @Get(':id')
  findOne(@Param('id', ParseIntPipe) id: number): any {
    // Thanks to ParseIntPipe, 'id' is guaranteed to be a number here.
    // If the original param was not a valid number, ParseIntPipe would throw an error.
    console.log(`Fetching item with ID: ${id}, type: ${typeof id}`);
    const item = this.items.find(item => item.id === id);
    if (!item) {
      throw new NotFoundException(`Item with ID ${id} not found`);
    }
    return item;
  }
}

// Ensure ItemsController is part of a module.

