const express = require('express');
const app = express();

app.get('/user/:id', (req, res, next) => {
  const userId = parseInt(req.params.id);
  if (isNaN(userId)) {
    const err = new Error('Invalid User ID: Must be a number.');
    err.status = 400; // Custom status code
    return next(err); // Pass error to error-handling middleware
  }
  res.send(`User ID is ${userId}`);
});

// Custom 404 Not Found handler (if no routes match)
app.use((req, res, next) => {
  res.status(404).send('Sorry, that route does not exist.');
});

// Custom error-handling middleware (must have 4 arguments)
app.use((err, req, res, next) => {
  console.error(err.stack); // Log the error stack for debugging
  const statusCode = err.status || 500; // Use error's status or default to 500
  res.status(statusCode).json({
    error: {
      message: err.message || 'Internal Server Error',
      status: statusCode
    }
  });
});

app.listen(3000);

