// Assume 'app' and 'bunny' sprite are set up and bunny is on stage
// bunny.x = 100; bunny.y = 100;

let speed = 5;
let directionX = 1;
let directionY = 1;

// Add a function to the ticker
app.ticker.add((delta) => {
  // 'delta' is a factor to make animation frame-rate independent.
  // For PIXI.Ticker, delta is by default 1 for 60FPS, 2 for 30FPS etc.
  // It represents elapsed frames. For time-based delta, use app.ticker.deltaTime.

  bunny.x += speed * directionX * delta;
  bunny.y += speed * directionY * delta;

  // Basic bounds check and bounce
  if (bunny.x > app.screen.width - bunny.width / 2 || bunny.x < bunny.width / 2) {
    directionX *= -1; // Reverse direction
  }
  if (bunny.y > app.screen.height - bunny.height / 2 || bunny.y < bunny.height / 2) {
    directionY *= -1; // Reverse direction
  }

  bunny.rotation += 0.01 * delta;
});

// To remove a function from the ticker:
// const myUpdate = (delta) => { /* ... */ };
// app.ticker.add(myUpdate);
// app.ticker.remove(myUpdate);

