// Assume 'app' is created.
// You need a sprite sheet JSON (e.g., from TexturePacker) and the corresponding image.
// Example: 'assets/spritesheet.json' and 'assets/spritesheet.png'

async function setupAnimation() {
  // Load the spritesheet data and texture
  const sheet = await PIXI.Assets.load('assets/spritesheet.json'); 
  // 'sheet.textures' now contains all individual frame textures
  // 'sheet.animations' might contain pre-defined animation sequences if in JSON

  // Example: Assuming your JSON defines an animation called 'walk_cycle'
  // Or manually create an array of textures for an animation
  const explosionFrames = [];
  for (let i = 0; i < 26; i++) { // Assuming 26 frames named like 'explosion_01.png', 'explosion_02.png'
     const frameKey = `Explosion_Sequence_A ${String(i + 1).padStart(5, '0')}.png`; // Adjust to your frame naming
     if (sheet.textures[frameKey]) {
        explosionFrames.push(sheet.textures[frameKey]);
     }
  }

  if (explosionFrames.length > 0) {
    const animatedExplosion = new PIXI.AnimatedSprite(explosionFrames);
    animatedExplosion.x = app.screen.width / 2;
    animatedExplosion.y = app.screen.height / 2;
    animatedExplosion.anchor.set(0.5);
    animatedExplosion.animationSpeed = 0.5; // Adjust speed (frames per ticker update)
    animatedExplosion.loop = false; // Set to true for looping animations
    animatedExplosion.play();
    app.stage.addChild(animatedExplosion);
  } else {
    console.error('Could not find frames for explosion animation.');
  }
}

if (typeof PIXI !== 'undefined') setupAnimation();

