// Assume 'app' (PIXI.Application) is created

// Create a container with some complex graphics
const complexGraphicsContainer = new PIXI.Container();
for (let i = 0; i < 10; i++) {
  const rect = new PIXI.Graphics()
    .beginFill(Math.random() * 0xFFFFFF)
    .drawRect(Math.random() * 180, Math.random() * 180, 20, 20)
    .endFill();
  complexGraphicsContainer.addChild(rect);
}

// Create a RenderTexture. The size should match the content you want to capture.
const renderTexture = PIXI.RenderTexture.create({ width: 200, height: 200 });

// Render the container to the render texture
// The second argument (true) clears the render texture before drawing
app.renderer.render(complexGraphicsContainer, { renderTexture: renderTexture, clear: true });

// Now you can use the renderTexture like any other texture
const spriteFromTexture = new PIXI.Sprite(renderTexture);
spriteFromTexture.x = 50;
spriteFromTexture.y = 50;
app.stage.addChild(spriteFromTexture);

// If complexGraphicsContainer changes, you'd need to re-render it to the renderTexture
// to update the spriteFromTexture's appearance.

